import React, { useRef, useState, useEffect } from 'react';
import PDFAutoScroll from './PDFAutoScroll';
import './PDFViewer.css';

// Import pdfjs library directly - explicitly set version for stability
import * as pdfjsLib from 'pdfjs-dist/build/pdf';

// Set the worker source explicitly
pdfjsLib.GlobalWorkerOptions.workerSrc = "https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.worker.min.js";

const PDFViewer = ({ pdfUrl }) => {
  const [numPages, setNumPages] = useState(0);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [pdfLoaded, setPdfLoaded] = useState(false);

  const containerRef = useRef(null);
  const pdfDocRef = useRef(null);
  const canvasRef = useRef(null);

  const pdfViewerRef = useRef({
    currentPageNumber: 1,
    pagesCount: 0,
    container: null
  });

  // Load the PDF document
  useEffect(() => {
    if (!pdfUrl) return;

    let isMounted = true;
    setIsLoading(true);
    setError(null);
    setPdfLoaded(false);

    // Function to handle PDF loading
    const loadPdf = async () => {
      try {
        let pdfData;

        // Handle different types of PDF sources
        if (pdfUrl.startsWith('blob:') || pdfUrl.startsWith('data:')) {
          // It's a blob URL or data URL, fetch it
          const response = await fetch(pdfUrl);
          const arrayBuffer = await response.arrayBuffer();
          pdfData = new Uint8Array(arrayBuffer);
        } else {
          // It's a regular URL
          pdfData = { url: pdfUrl, withCredentials: false };
        }

        // Load the PDF
        const pdf = await pdfjsLib.getDocument(pdfData).promise;

        if (!isMounted) return;

        // Store the pdf document
        pdfDocRef.current = pdf;

        // Set the total page count
        setNumPages(pdf.numPages);
        pdfViewerRef.current.pagesCount = pdf.numPages;
        setPageNumber(1);
        setPdfLoaded(true);
        setIsLoading(false);
      } catch (error) {
        if (!isMounted) return;
        console.error('Error loading PDF:', error);
        setError(`Failed to load PDF: ${error.message}`);
        setIsLoading(false);
      }
    };

    loadPdf();

    // Cleanup
    return () => {
      isMounted = false;
      if (pdfDocRef.current) {
        try {
          pdfDocRef.current.destroy();
          pdfDocRef.current = null;
        } catch (e) {
          console.error('Error destroying PDF document:', e);
        }
      }
    };
  }, [pdfUrl]);

  // Render the current page when page number changes
  useEffect(() => {
    if (!pdfLoaded || !pdfDocRef.current || !canvasRef.current || !pageNumber) {
      return;
    }

    let isMounted = true;

    const renderPage = async () => {
      try {
        // Get the page
        const page = await pdfDocRef.current.getPage(pageNumber);

        if (!isMounted || !canvasRef.current) {
          page.cleanup && page.cleanup();
          return;
        }

        // Get the viewport at scale 1.0
        const viewport = page.getViewport({ scale: 1.0 });

        // Get the canvas and context
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d', { alpha: false });

        // Clear the canvas
        context.clearRect(0, 0, canvas.width, canvas.height);

        // Set canvas dimensions for high quality
        const adjustedScale = 2.0;
        canvas.width = viewport.width * adjustedScale;
        canvas.height = viewport.height * adjustedScale;

        // Scale down the canvas for display
        canvas.style.width = (viewport.width) + 'px';
        canvas.style.height = (viewport.height) + 'px';

        // Set scale for context
        context.scale(adjustedScale, adjustedScale);

        // Render the page
        await page.render({
          canvasContext: context,
          viewport: viewport
        }).promise;

        // Clean up page resources
        page.cleanup && page.cleanup();

        // Update auto-scroll refs
        pdfViewerRef.current.currentPageNumber = pageNumber;
        if (containerRef.current) {
          pdfViewerRef.current.container = containerRef.current;
        }
      } catch (error) {
        if (!isMounted) return;
        console.error('Error rendering page:', error);
        setError(`Error rendering page ${pageNumber}: ${error.message}`);
      }
    };

    renderPage();

    return () => {
      isMounted = false;
    };
  }, [pageNumber, pdfLoaded, scale]);

  // Update zoom when scale changes
  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;

    // Get original dimensions (stored in data attributes or from style)
    const originalWidth = parseFloat(canvas.getAttribute('data-original-width') || 
                                    canvas.style.width || 
                                    canvas.width);
    const originalHeight = parseFloat(canvas.getAttribute('data-original-height') || 
                                     canvas.style.height || 
                                     canvas.height);

    // If we don't have original dimensions stored, save them
    if (!canvas.getAttribute('data-original-width')) {
      canvas.setAttribute('data-original-width', originalWidth);
      canvas.setAttribute('data-original-height', originalHeight);
    }

    // Apply zoom to canvas
    canvas.style.width = (originalWidth * scale) + 'px';
    canvas.style.height = (originalHeight * scale) + 'px';
  }, [scale]);

  // Handle page navigation
  const changePage = (offset) => {
    if (!numPages) return;

    // Create and dispatch an event to notify auto-scroll component to reset timer
    const resetTimerEvent = new CustomEvent('resetAutoScrollTimer');
    document.dispatchEvent(resetTimerEvent);

    setPageNumber(prevPageNumber => {
      const newPageNumber = prevPageNumber + offset;
      return Math.min(Math.max(1, newPageNumber), numPages);
    });
  };

  // Handle zoom controls
  const zoomIn = () => setScale(prevScale => Math.min(prevScale + 0.1, 3.0));
  const zoomOut = () => setScale(prevScale => Math.max(prevScale - 0.1, 0.5));
  const resetZoom = () => setScale(1.0);

  // Listen for page change events from auto-scroll
  useEffect(() => {
    const handlePageChange = (event) => {
      const { newPage } = event.detail;
      if (newPage && newPage <= numPages) {
        setPageNumber(newPage);
      }
    };

    document.addEventListener('pageChange', handlePageChange);

    return () => {
      document.removeEventListener('pageChange', handlePageChange);
    };
  }, [numPages]);

  return (
    <div className="pdf-viewer">
      <div className="pdf-toolbar">
        <div className="page-navigation">
          <button 
            onClick={() => changePage(-1)} 
            disabled={pageNumber <= 1 || isLoading}
          >
            Previous (z)
          </button>
          <span>
            Page {pageNumber} of {numPages || '--'}
          </span>
          <button 
            onClick={() => changePage(1)} 
            disabled={pageNumber >= numPages || isLoading}
            id="next-page-button"
          >
            Next (m)
          </button>
        </div>

        <div className="zoom-controls">
          <button onClick={zoomOut} disabled={isLoading}>-</button>
          <span>{Math.round(scale * 100)}%</span>
          <button onClick={zoomIn} disabled={isLoading}>+</button>
          <button onClick={resetZoom} disabled={isLoading}>Reset</button>
        </div>

        <PDFAutoScroll pdfViewerRef={pdfViewerRef} />
      </div>

      <div className="pdf-container" ref={containerRef}>
        {error && <div className="pdf-error">{error}</div>}
        {isLoading && <div className="pdf-loading">Loading PDF document...</div>}
        {pdfLoaded && !isLoading && !error && (
          <canvas ref={canvasRef} className="pdf-page"></canvas>
        )}
      </div>
    </div>
  );
};

export default PDFViewer;
